// src/pages/RegisterPage.js
import React, { useState } from 'react';
import '../styles/common.css'; // Uses your team-wide CSS

function RegisterPage() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');
    if (!formData.email.includes('@')) {
      setError('Invalid email format.');
      return;
    }
    if (formData.password.length < 6) {
      setError('Password must be at least 6 characters.');
      return;
    }
    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match.');
      return;
    }
    setSuccess(true);
    setTimeout(() => {
      window.location.href = '/role-select';
    }, 1500);
  };

  return (
    <div className="form-container">
      <form onSubmit={handleSubmit} className="form-card">
        <h2>Register</h2>
        <div className="form-field">
          <label>Full Name</label>
          <input type="text" name="name" value={formData.name} onChange={handleChange} required />
        </div>
        <div className="form-field">
          <label>Email Address</label>
          <input type="email" name="email" value={formData.email} onChange={handleChange} required />
        </div>
        <div className="form-field">
          <label>Password</label>
          <input type="password" name="password" value={formData.password} onChange={handleChange} required />
        </div>
        <div className="form-field">
          <label>Confirm Password</label>
          <input type="password" name="confirmPassword" value={formData.confirmPassword} onChange={handleChange} required />
        </div>

        {error && <div className="form-error">{error}</div>}
        {success && <div className="form-success">âœ… Registration successful!</div>}

        <button type="submit" className="submit-btn">Sign Up</button>

        <div className="switch-page">
          <p>Already have an account? <button className="link-btn" disabled>Login</button></p>
        </div>

        <div className="switch-page">
          <p>Or sign in with:</p>
          <button className="submit-btn" style={{ backgroundColor: '#4267B2' }}>Login with Facebook</button>
          <button className="submit-btn" style={{ backgroundColor: '#DB4437' }}>Login with Google</button>
        </div>
      </form>
    </div>
  );
}

export default RegisterPage;
