import React, { useState } from 'react';
import '../styles/common.css';

function RoleSelectionPage() {
  const [selectedRole, setSelectedRole] = useState('');
  const [userDetails, setUserDetails] = useState({
    fullName: '',
    dob: '',
    phone: '',
    location: '',
  });
  const [organiserDetails, setOrganiserDetails] = useState({
    fullName: '',
    organisationName: '',
    roleTitle: '',
    eventTypes: [],
    contactNumber: '',
    website: '',
  });

  const handleRoleSelect = (role) => {
    setSelectedRole(role);
  };

  const handleUserChange = (e) => {
    setUserDetails({ ...userDetails, [e.target.name]: e.target.value });
  };

  const handleOrganiserChange = (e) => {
    setOrganiserDetails({ ...organiserDetails, [e.target.name]: e.target.value });
  };

  const handleEventTypeToggle = (eventType) => {
    setOrganiserDetails((prev) => {
      const alreadySelected = prev.eventTypes.includes(eventType);
      return {
        ...prev,
        eventTypes: alreadySelected
          ? prev.eventTypes.filter(type => type !== eventType)
          : [...prev.eventTypes, eventType],
      };
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (selectedRole === 'User') {
      console.log('User Info:', userDetails);
    } else {
      console.log('Organiser Info:', organiserDetails);
    }
  };

  return (
    <div className="reg-page-bg">
      <div className="register-card">
        {!selectedRole ? (
          <>
            <h2 className="register-title">What is your purpose for joining?</h2>
            <div className="role-selector">
              <button onClick={() => handleRoleSelect('User')} className="signup-btn">I want to attend events</button>
              <button onClick={() => handleRoleSelect('Organiser')} className="signup-btn">I want to host events</button>
            </div>
          </>
        ) : (
          <form onSubmit={handleSubmit} className="register-form">
            {selectedRole === 'User' ? (
              <>
                <h2 className="register-title">User Details</h2>
                <div className="form-field">
                  <label>Full Name:</label>
                  <input name="fullName" value={userDetails.fullName} onChange={handleUserChange} required />
                </div>
                <div className="form-field">
                  <label>Date of Birth:</label>
                  <input type="date" name="dob" value={userDetails.dob} onChange={handleUserChange} required />
                </div>
                <div className="form-field">
                  <label>Phone Number:</label>
                  <input name="phone" value={userDetails.phone} onChange={handleUserChange} required />
                </div>
                <div className="form-field">
                  <label>Location:</label>
                  <input name="location" value={userDetails.location} onChange={handleUserChange} required />
                </div>
              </>
            ) : (
              <>
                <h2 className="register-title">Organiser Details</h2>
                <div className="form-field">
                  <label>Full Name:</label>
                  <input name="fullName" value={organiserDetails.fullName} onChange={handleOrganiserChange} required />
                </div>
                <div className="form-field">
                  <label>Organisation Name:</label>
                  <input name="organisationName" value={organiserDetails.organisationName} onChange={handleOrganiserChange} required />
                </div>
                <div className="form-field">
                  <label>Role Title:</label>
                  <input name="roleTitle" value={organiserDetails.roleTitle} onChange={handleOrganiserChange} required />
                </div>
                <div className="form-field">
                  <label>Contact Number:</label>
                  <input name="contactNumber" value={organiserDetails.contactNumber} onChange={handleOrganiserChange} required />
                </div>
                <div className="form-field">
                  <label>Website:</label>
                  <input name="website" value={organiserDetails.website} onChange={handleOrganiserChange} />
                </div>
                <div className="form-field">
                  <label>Event Types:</label>
                  {['Conference', 'Concert', 'Workshop', 'Meetup'].map(type => (
                    <label key={type}>
                      <input
                        type="checkbox"
                        checked={organiserDetails.eventTypes.includes(type)}
                        onChange={() => handleEventTypeToggle(type)}
                      /> {type}
                    </label>
                  ))}
                </div>
              </>
            )}
            <button type="submit" className="signup-btn">Submit</button>
          </form>
        )}
      </div>
    </div>
  );
}

export default RoleSelectionPage;
