import React, { useState } from 'react';

function OrganiserDetailForm() {
  const [formData, setFormData] = useState({
    fullName: '',
    organisation: '',
    roleTitle: '',
    contact: '',
    website: '',
    eventTypes: [],
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (type === 'checkbox') {
      setFormData((prev) => ({
        ...prev,
        eventTypes: checked
          ? [...prev.eventTypes, value]
          : prev.eventTypes.filter((t) => t !== value),
      }));
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("Organiser details submitted!");
  };

  return (
    <div className="reg-page-bg">
      <div className="register-card">
        <h2 className="register-title">Organiser Details</h2>
        <form className="register-form" onSubmit={handleSubmit}>
          <label>Full Name</label>
          <input name="fullName" onChange={handleChange} required />

          <label>Organisation Name</label>
          <input name="organisation" onChange={handleChange} required />

          <label>Role Title</label>
          <input name="roleTitle" onChange={handleChange} required />

          <label>Contact Number</label>
          <input name="contact" onChange={handleChange} required />

          <label>Website</label>
          <input name="website" onChange={handleChange} />

          <label>Event Types</label>
          {["Conference", "Concert", "Workshop", "Meetup"].map((type) => (
            <label key={type}>
              <input
                type="checkbox"
                value={type}
                onChange={handleChange}
                checked={formData.eventTypes.includes(type)}
              />
              {type}
            </label>
          ))}

          <button className="signup-btn" type="submit">Submit</button>
        </form>
      </div>
    </div>
  );
}

export default OrganiserDetailForm;
