import React, { useState } from 'react';
import '../styles/common.css';  // import common styles

function Register() {
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [role, setRole] = useState('User');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);

  const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');

    if (!emailPattern.test(email)) {
      setError('Please enter a valid email address.');
      return;
    }
    if (password.length < 6) {
      setError('Password must be at least 6 characters long.');
      return;
    }
    if (password !== confirmPassword) {
      setError('Passwords do not match. Please check.');
      return;
    }

    setSuccess(true);
  };

  return (
    <div className="form-container">
      <h2>Register an Account</h2>
      <form onSubmit={handleSubmit} className="form-card">
        <div className="form-field">
          <label htmlFor="fullName">Full Name:</label>
          <input 
            type="text" id="fullName" value={fullName} required 
            onChange={(e) => setFullName(e.target.value)} 
          />
        </div>
        <div className="form-field">
          <label htmlFor="email">Email:</label>
          <input 
            type="email" id="email" value={email} required 
            onChange={(e) => setEmail(e.target.value)} 
          />
        </div>
        <div className="form-field">
          <label htmlFor="password">Password:</label>
          <input 
            type="password" id="password" value={password} required 
            onChange={(e) => setPassword(e.target.value)} 
            minLength={6}
          />
        </div>
        <div className="form-field">
          <label htmlFor="confirmPassword">Confirm Password:</label>
          <input 
            type="password" id="confirmPassword" value={confirmPassword} required 
            onChange={(e) => setConfirmPassword(e.target.value)} 
            minLength={6}
          />
        </div>

        <div className="role-selector">
          <span>Select Role:</span>
          <button 
            type="button" 
            className={`role-option ${role === 'User' ? 'active' : ''}`}
            onClick={() => setRole('User')}
          >
            User
          </button>
          <button 
            type="button" 
            className={`role-option ${role === 'Organiser' ? 'active' : ''}`}
            onClick={() => setRole('Organiser')}
          >
            Organiser
          </button>
        </div>

        {error && <div className="form-error">{error}</div>}
        {success && <div className="form-success">✅ Registration successful!</div>}

        <button type="submit" className="submit-btn">Register</button>
      </form>
    </div>
  );
}

export default Register;
