import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/common.css';  // import common styles

function Register() {
  const navigate = useNavigate();
  // State for form fields
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [role, setRole] = useState('User');      // default role
  const [error, setError] = useState('');        // error message state
  const [success, setSuccess] = useState(false); // success state

  // Simple email regex for validation (must have @ and . in the right places)
  const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');   // reset error each submit
    // Validation checks
    if (!emailPattern.test(email)) {
      setError('Please enter a valid email address.');
      return;
    }
    if (password.length < 6) {
      setError('Password must be at least 6 characters long.');
      return;
    }
    if (password !== confirmPassword) {
      setError('Passwords do not match. Please check.');
      return;
    }
    // (Optional: ensure a role is selected; in this implementation, role is always set to a default)
    // If all validations pass:
    setSuccess(true);
    // Simulate successful registration message and redirect to Login after a short delay
    setTimeout(() => {
      navigate('/login');  // navigate to login page
    }, 1500);
  };

  return (
    <div className="form-container">
      <h2>Register an Account</h2>
      <form onSubmit={handleSubmit} className="form-card">
        {/* Full Name Field */}
        <div className="form-field">
          <label htmlFor="fullName">Full Name:</label>
          <input 
            type="text" id="fullName" value={fullName} required 
            onChange={(e) => setFullName(e.target.value)} 
          />
        </div>
        {/* Email Field */}
        <div className="form-field">
          <label htmlFor="email">Email:</label>
          <input 
            type="email" id="email" value={email} required 
            onChange={(e) => setEmail(e.target.value)} 
          />
        </div>
        {/* Password Field */}
        <div className="form-field">
          <label htmlFor="password">Password:</label>
          <input 
            type="password" id="password" value={password} required 
            onChange={(e) => setPassword(e.target.value)} 
            minLength={6}
          />
        </div>
        {/* Confirm Password Field */}
        <div className="form-field">
          <label htmlFor="confirmPassword">Confirm Password:</label>
          <input 
            type="password" id="confirmPassword" value={confirmPassword} required 
            onChange={(e) => setConfirmPassword(e.target.value)} 
            minLength={6}
          />
        </div>
        {/* Role Selection Buttons */}
        <div className="role-selector">
          <span>Select Role:</span>
          <button 
            type="button" 
            className={`role-option ${role === 'User' ? 'active' : ''}`}
            onClick={() => setRole('User')}
          >
            User
          </button>
          <button 
            type="button" 
            className={`role-option ${role === 'Organiser' ? 'active' : ''}`}
            onClick={() => setRole('Organiser')}
          >
            Organiser
          </button>
        </div>
        {/* Error message (if any) */}
        {error && <div className="form-error">{error}</div>}
        {/* Success message (if any) */}
        {success && <div className="form-success">âœ… Registration successful! Redirecting to login...</div>}
        {/* Submit Button */}
        <button type="submit" className="submit-btn">Register</button>
      </form>
    </div>
  );
}

export default Register;
