import React, { useState } from 'react';
import "../styles/RoleSelectionPage.css";

function RoleSelectionPage() {
  const [roleSelected, setRoleSelected] = useState(null);
  const [userDetails, setUserDetails] = useState({
    fullName: "",
    age: "",
    phone: "",
    location: ""
  });
  const [orgDetails, setOrgDetails] = useState({
    fullName: "",
    orgName: "",
    roleTitle: "",
    eventTypes: []
  });

  const handleUserChange = (e) => {
    const { name, value } = e.target;
    setUserDetails(prev => ({ ...prev, [name]: value }));
  };

  const handleOrgChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (type === "checkbox") {
      setOrgDetails(prev => ({
        ...prev,
        eventTypes: checked
          ? [...prev.eventTypes, value]
          : prev.eventTypes.filter(ev => ev !== value)
      }));
    } else {
      setOrgDetails(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleUserSubmit = (e) => {
    e.preventDefault();
    console.log("User submitted:", userDetails);
    alert("User profile submitted!");
  };

  const handleOrgSubmit = (e) => {
    e.preventDefault();
    console.log("Organiser submitted:", orgDetails);
    alert("Organiser profile submitted!");
  };

  return (
    <div className="role-selection-page">
      <div className="role-card">
        <div className="role-options">
          <button 
            className={`role-button ${roleSelected === "user" ? "selected" : ""}`} 
            onClick={() => setRoleSelected("user")}
          >User</button>
          <button 
            className={`role-button ${roleSelected === "organiser" ? "selected" : ""}`} 
            onClick={() => setRoleSelected("organiser")}
          >Organiser</button>
        </div>

        {roleSelected === "user" && (
          <form className="role-form" onSubmit={handleUserSubmit}>
            <div className="form-group">
              <label>Full Name:</label>
              <input name="fullName" value={userDetails.fullName} onChange={handleUserChange} />
            </div>
            <div className="form-group">
              <label>Age:</label>
              <input name="age" value={userDetails.age} onChange={handleUserChange} />
            </div>
            <div className="form-group">
              <label>Phone Number:</label>
              <input name="phone" value={userDetails.phone} onChange={handleUserChange} />
            </div>
            <div className="form-group">
              <label>Location:</label>
              <input name="location" value={userDetails.location} onChange={handleUserChange} />
            </div>
            <button type="submit" className="submit-button">Submit</button>
          </form>
        )}

        {roleSelected === "organiser" && (
          <form className="role-form" onSubmit={handleOrgSubmit}>
            <div className="form-group">
              <label>Full Name:</label>
              <input name="fullName" value={orgDetails.fullName} onChange={handleOrgChange} />
            </div>
            <div className="form-group">
              <label>Organisation Name:</label>
              <input name="orgName" value={orgDetails.orgName} onChange={handleOrgChange} />
            </div>
            <div className="form-group">
              <label>Role Title:</label>
              <input name="roleTitle" value={orgDetails.roleTitle} onChange={handleOrgChange} />
            </div>
            <div className="form-group">
              <label>Event Types:</label>
              <div className="checkbox-group">
                {['Conference', 'Concert', 'Workshop', 'Meetup'].map(type => (
                  <label key={type}>
                    <input
                      type="checkbox"
                      name="eventTypes"
                      value={type}
                      checked={orgDetails.eventTypes.includes(type)}
                      onChange={handleOrgChange}
                    /> {type}
                  </label>
                ))}
              </div>
            </div>
            <button type="submit" className="submit-button">Submit</button>
          </form>
        )}
      </div>
    </div>
  );
}

export default RoleSelectionPage;
