// src/pages/RegisterPage.js
import React, { useState } from 'react';
import '../styles/common.css';

function RegisterPage() {
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');
    setSuccess(false);

    if (!formData.email.includes('@')) {
      setError('Please enter a valid email.');
      return;
    }
    if (formData.password.length < 6) {
      setError('Password must be at least 6 characters.');
      return;
    }
    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match.');
      return;
    }

    setSuccess(true);
  };

  return (
    <div className="reg-page-bg">
      <div className="register-card">
        <h2 className="register-title" style={{ textAlign: 'center', color: 'black' }}>Registration</h2>
        <form onSubmit={handleSubmit} className="register-form">
          <div style={{ textAlign: 'left' }}>
            <label style={{ fontWeight: 'bold' }}>Full Name</label>
            <input
              type="text"
              name="fullName"
              placeholder="Enter full name"
              value={formData.fullName}
              onChange={handleChange}
              required
            />
          </div>

          <div style={{ textAlign: 'left' }}>
            <label style={{ fontWeight: 'bold' }}>Email Address</label>
            <input 
              type="email" 
              name="email" 
              placeholder="Enter email" 
              value={formData.email} 
              onChange={handleChange} 
              required 
            />
          </div>

          <div style={{ textAlign: 'left' }}>
            <label style={{ fontWeight: 'bold' }}>Password</label>
            <input 
              type="password" 
              name="password" 
              placeholder="Create password" 
              value={formData.password} 
              onChange={handleChange} 
              required 
            />
          </div>

          <div style={{ textAlign: 'left' }}>
            <label style={{ fontWeight: 'bold' }}>Confirm Password</label>
            <input 
              type="password" 
              name="confirmPassword" 
              placeholder="Confirm password" 
              value={formData.confirmPassword} 
              onChange={handleChange} 
              required 
            />
          </div>

          <button type="submit" className="signup-btn">Sign Up</button>

          {error && <div className="form-error">{error}</div>}
          {success && <div className="form-success">âœ… Registered!</div>}

          <p className="redirect-text">
            Already have an account? <a href="/login">Login</a>
          </p>

          <div className="divider"><span>Or sign in with:</span></div>

          <button type="button" className="facebook-btn">Login with Facebook</button>
          <button type="button" className="google-btn">Login with Google</button>
        </form>
      </div>
    </div>
  );
}

export default RegisterPage;
