import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function OrganiserDetailForm() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    fullName: '',
    orgName: '',
    roleTitle: '',
    contact: '',
    website: '',
    eventTypes: [],
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (type === 'checkbox') {
      setFormData((prev) => ({
        ...prev,
        eventTypes: checked
          ? [...prev.eventTypes, value]
          : prev.eventTypes.filter((t) => t !== value),
      }));
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("Organiser details submitted!");
  };

  return (
    <div className="reg-page-bg">
      <button
        onClick={() => navigate('/select-role')}
        style={{
          position: 'absolute',
          top: '20px',
          left: '20px',
          padding: '6px 12px',
          backgroundColor: '#99FFFF',
          border: 'none',
          fontWeight: 'bold',
          borderRadius: '4px',
          cursor: 'pointer'
        }}
      >
        ← Back
      </button>

      <div className="register-card">
        <h2 className="register-title">Organiser Details</h2>
        <form className="register-form" onSubmit={handleSubmit}>
          <label style={{ fontWeight: 'bold' }}>Full Name:</label>
          <input name="fullName" onChange={handleChange} required />

          <label style={{ fontWeight: 'bold' }}>Organisation Name:</label>
          <input name="orgName" onChange={handleChange} required />

          <label style={{ fontWeight: 'bold' }}>Role Title:</label>
          <input name="roleTitle" onChange={handleChange} required />

          <label style={{ fontWeight: 'bold' }}>Contact Number:</label>
          <input name="contact" onChange={handleChange} required />

          <label style={{ fontWeight: 'bold' }}>Website:</label>
          <input name="website" onChange={handleChange} />

          <label style={{ fontWeight: 'bold' }}>Event Types:</label>
          <label><input type="checkbox" name="eventTypes" value="Conference" onChange={handleChange} /> Conference</label>
          <label><input type="checkbox" name="eventTypes" value="Concert" onChange={handleChange} /> Concert</label>
          <label><input type="checkbox" name="eventTypes" value="Workshop" onChange={handleChange} /> Workshop</label>
          <label><input type="checkbox" name="eventTypes" value="Meetup" onChange={handleChange} /> Meetup</label>

          <button className="signup-btn" type="submit">Submit</button>
        </form>
      </div>
    </div>
  );
}

export default OrganiserDetailForm;
